mapper.update:
UPDATE REC_BOARD
SET views = views + 1
WHERE recommendation_no = #{recommendationNo}

UPDATE REC_FILES
SET original_name = #{originalName},
saved_name = #{savedName}
WHERE recommendation_no = #{recommendationNo}

mapper.select:
SELECT COUNT(*) > 0
FROM REC_HEARTBTN
WHERE user_no = #{userNo} AND recommendation_no = #{recommendationNo}
