mapper.update:
	UPDATE USERS
	SET
		nickname = #{nickname},
		phone = #{phone},
		email = #{email}
    WHERE user_no = #{userNo}

	UPDATE USERS
	SET user_pw = #{userPw}
    WHERE user_no = #{userNo}

mapper.select:
	SELECT COUNT(1) > 0 
    FROM users 
    WHERE user_id = #{userId}

	SELECT COUNT(1) > 0 
    FROM users 
    WHERE nickname = #{nickname}
